  GET_USER_ARTICLE ({ commit, dispatch, state }, parameter) {
    // 获取文章
    return fetch({
      url: '/user-article',
      method: 'get',
      parameter: { params: parameter }
    })
  },
  UPDATE_ARTICLE: ({ commit, dispatch, state }, data) => {
    return fetch({
      url: '/article/update',
      method: 'put',
      parameter: data
    })
  }
}

export default {
  namespaced: true,
  state,
  mutations,
  actions
}

