class Share {
  // 分享到新浪
  static shareToXl (title, url, picurl) {
    var sharesinastring =
      'http://v.t.sina.com.cn/share/share.php?title=' +
      title +
      '&url=' +
      url +
      '&content=utf-8&sourceUrl=' +
      url +
      '&pic=' +
      picurl
    window.open(
      sharesinastring,
      'newwindow',
      'height=400,width=400,top=100,left=100'
    )
  }

  // 分享到qq空间
  static shareToQq (title, url, picurl) {
    var shareqqzonestring =
      'http://sns.qzone.qq.com/cgi-bin/qzshare/cgi_qzshare_onekey?summary=' +
      title +
      '&url=' +
      url +
      '&pics=' +
      picurl
    window.open(
      shareqqzonestring,
      'newwindow',
      'height=400,width=400,top=100,left=100'
    )
  }

  // 分享到qq
  static shareQQ (title, url, picurl) {
    var shareqqzonestring = `https://connect.qq.com/widget/shareqq/index.html?url=${url}?sharesource=qzone&title=${title}&pics=${picurl}&summary=${title}`
    window.open(
      shareqqzonestring,
      'newwindow',
      'height=400,width=400,top=100,left=100'
    )
  }
}

export default Share

import isProduction from './isProduction' // process.env.NODE_ENV === 'production'

class Baidu {
  // 百度的自动推送
  static resource ({ route, config = {}, random = '' }) {
    let resourceArray = []
    if (isProduction && config.isBaiduAuthPush === 'yes') {
      // 百度seo方面 连接自动推送
      resourceArray.push({
        src: `https://zz.bdstatic.com/linksubmit/push.js?_random=${route.name +
          random}`,
        type: 'text/javascript'
      })
    }
    return resourceArray
  }
}

export default Baidu

import isProduction from './isProduction'

class Google {
  // 逐入google统计
  static statisticsCode ({ route, googleCode = '', random = '' }) {
    let resourceArray = []
    if (isProduction && googleCode) {
      // 百度seo方面 连接自动推送
      resourceArray.push({
        src: `https://www.googletagmanager.com/gtag/js?id=${googleCode}&_random=${route.name +
          random}`,
        type: 'text/javascript'
      })
    }
    return resourceArray
  }

  // 逐入google统计code
  static injectionGoogleCode (googleCode = '') {
    if (isProduction && googleCode) {
      window.dataLayer = window.dataLayer || []
      function gtag () {
        dataLayer.push(arguments)
      }
      gtag('js', new Date())
      gtag('config', googleCode)
    }
  }
}

export default Google

